import { useEffect, useReducer, useState } from "react"
import Render from "./Render"

const initState = {
    toDos: []
}

const reducer = (state, action) => {
    if (action.type === 'CREATE') {
        return {
            toDos: [...state.toDos, action.payload]
        }
    }
    else if (action.type === 'DELETE') {
        const newTodos = state.toDos.filter(toDo => toDo)
        return {

        }
    }
}

const Form = (props) => {
    const [toDo, setTitle] = useState({})
    const [state, dispatch] = useReducer(reducer, initState)

    const submitHandler = (e) => {
        e.preventDefault()
        toDo ? dispatch({ type: 'CREATE', payload: toDo }) : alert('Please insert todo before submit')
        setTitle({})
    }

    // useEffect(() => console.log(toDo), [toDo])

    return (
        <>
            <div>
                <form action="" onSubmit={submitHandler}>
                    <div className="input-box">
                        <label htmlFor="todo">Create ToDo</label>
                        <input type="text" onChange={e => setTitle({
                            id: Date.now().toLocaleString(),
                            title: e.target.value
                        })} value={toDo.title} />
                    </div>
                    <div className="button">
                        <button>Create</button>
                    </div>
                </form>
            </div>
            {/* <Render toDos={toDo.toDos} /> */}
            <Render toDos={state.toDos} delete={dispatch} />
        </>
    )
}

export default Form
