import Form from "./Form"
import Render from "./Render"

const ToDo = () => {
    const [title, setTitle] = useState('')
    const [toDo, dispatch] = useReducer(reducer, initState)

    const submitHandler = (e) => {
        e.preventDefault()
        title ? dispatch({ type: 'CREATE', payload: title }) : alert('Please insert todo before submit')
        setTitle('')
    }

    useEffect(() => console.log(toDo), [toDo])
    return (
        <>
            <Form submit={submitHandler} />
            <Render />
        </>
    )
}

export default ToDo
